            const filteredMonsters = this.state.monsters.filter((monster) => {
              return monster.name.toLowerCase().includes(searchString);
            });

            this.setState(
              () => {
                return { monsters: filteredMonsters };
              },
              () => {
                console.log("this.state input =>", this.state);
              }
            );
          }}
        />
